    /// @dev Constructs an outcome collection ID from a parent collection and an outcome collection.
    /// @param parentCollectionId Collection ID of the parent outcome collection, or bytes32(0) if there's no parent.
    /// @param conditionId Condition ID of the outcome collection to combine with the parent outcome collection.
    /// @param indexSet Index set of the outcome collection to combine with the parent outcome collection.
    function getCollectionId(bytes32 parentCollectionId, bytes32 conditionId, uint indexSet) internal view returns (bytes32) {
        uint x1 = uint(keccak256(abi.encodePacked(conditionId, indexSet)));
        bool odd = x1 >> 255 != 0;
        uint y1;
        uint yy;
        do {
            x1 = addmod(x1, 1, P);
            yy = addmod(mulmod(x1, mulmod(x1, x1, P), P), B, P);
            y1 = sqrt(yy);
        } while(mulmod(y1, y1, P) != yy);
        if(odd && y1 % 2 == 0 || !odd && y1 % 2 == 1)
            y1 = P - y1;

        uint x2 = uint(parentCollectionId);
        if(x2 != 0) {
            odd = x2 >> 254 != 0;
            x2 = (x2 << 2) >> 2;
            yy = addmod(mulmod(x2, mulmod(x2, x2, P), P), B, P);
            uint y2 = sqrt(yy);
            if(odd && y2 % 2 == 0 || !odd && y2 % 2 == 1)
                y2 = P - y2;
            require(mulmod(y2, y2, P) == yy, "invalid parent collection ID");

            (bool success, bytes memory ret) = address(6).staticcall(abi.encode(x1, y1, x2, y2));
            require(success, "ecadd failed");
            (x1, y1) = abi.decode(ret, (uint, uint));
        }

        if(y1 % 2 == 1)
            x1 ^= 1 << 254;

        return bytes32(x1);
    }

    /// @dev Constructs a position ID from a collateral token and an outcome collection. These IDs are used as the ERC-1155 ID for this contract.
    /// @param collateralToken Collateral token which backs the position.
    /// @param collectionId ID of the outcome collection associated with this position.
    function getPositionId(IERC20 collateralToken, bytes32 collectionId) internal pure returns (uint) {
        return uint(keccak256(abi.encodePacked(collateralToken, collectionId)));
    }
}

contracts/IConditionalTokens.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC1155 } from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

interface IConditionalTokens is IERC1155 {

    /// @dev Emitted upon the successful preparation of a condition.
    /// @param conditionId The condition's ID. This ID may be derived from the other three parameters via ``keccak256(abi.encodePacked(oracle, questionId, outcomeSlotCount))``.
    /// @param oracle The account assigned to report the result for the prepared condition.
    /// @param questionId An identifier for the question to be answered by the oracle.
    /// @param outcomeSlotCount The number of outcome slots which should be used for this condition. Must not exceed 256.
    event ConditionPreparation(
        bytes32 indexed conditionId,
        address indexed oracle,
        bytes32 indexed questionId,
        uint outcomeSlotCount
    );

    event ConditionResolution(
        bytes32 indexed conditionId,
        address indexed oracle,
        bytes32 indexed questionId,
        uint outcomeSlotCount,
        uint[] payoutNumerators
    );

    /// @dev Emitted when a position is successfully split.
    event PositionSplit(
        address indexed stakeholder,
        IERC20 collateralToken,
        bytes32 indexed parentCollectionId,
        bytes32 indexed conditionId,
        uint[] partition,
        uint amount
    );
    /// @dev Emitted when positions are successfully merged.
    event PositionsMerge(
        address indexed stakeholder,
        IERC20 collateralToken,
        bytes32 indexed parentCollectionId,
        bytes32 indexed conditionId,
        uint[] partition,
        uint amount
    );
    event PayoutRedemption(
        address indexed redeemer,
        IERC20 indexed collateralToken,
        bytes32 indexed parentCollectionId,
        bytes32 conditionId,
        uint[] indexSets,
        uint payout
    );

    /// Mapping key is an condition ID. Value represents numerators of the payout vector associated with the condition. This array is initialized with a length equal to the outcome slot count. E.g. Condition with 3 outcomes [A, B, C] and two of those correct [0.5, 0.5, 0]. In Ethereum there are no decimal values, so here, 0.5 is represented by fractions like 1/2 == 0.5. That's why we need numerator and denominator values. Payout numerators are also used as a check of initialization. If the numerators array is empty (has length zero), the condition was not created/prepared. See getOutcomeSlotCount.
    function payoutNumerators(bytes32 b) external view returns (uint[] memory);
    /// Denominator is also used for checking if the condition has been resolved. If the denominator is non-zero, then the condition has been resolved.
    function payoutDenominator(bytes32 b) external view returns (uint);

    /// @dev This function prepares a condition by initializing a payout vector associated with the condition.
    /// @param oracle The account assigned to report the result for the prepared condition.
    /// @param questionId An identifier for the question to be answered by the oracle.
    /// @param outcomeSlotCount The number of outcome slots which should be used for this condition. Must not exceed 256.
    function prepareCondition(address oracle, bytes32 questionId, uint outcomeSlotCount) external;

    /// @dev Called by the oracle for reporting results of conditions. Will set the payout vector for the condition with the ID ``keccak256(abi.encodePacked(oracle, questionId, outcomeSlotCount))``, where oracle is the message sender, questionId is one of the parameters of this function, and outcomeSlotCount is the length of the payouts parameter, which contains the payoutNumerators for each outcome slot of the condition.
    /// @param questionId The question ID the oracle is answering for
    /// @param payouts The oracle's answer
    function reportPayouts(bytes32 questionId, uint[] calldata payouts) external;

    /// @dev This function splits a position. If splitting from the collateral, this contract will attempt to transfer `amount` collateral from the message sender to itself. Otherwise, this contract will burn `amount` stake held by the message sender in the position being split worth of EIP 1155 tokens. Regardless, if successful, `amount` stake will be minted in the split target positions. If any of the transfers, mints, or burns fail, the transaction will revert. The transaction will also revert if the given partition is trivial, invalid, or refers to more slots than the condition is prepared with.
    /// @param collateralToken The address of the positions' backing collateral token.
    /// @param parentCollectionId The ID of the outcome collections common to the position being split and the split target positions. May be null, in which only the collateral is shared.
    /// @param conditionId The ID of the condition to split on.
    /// @param partition An array of disjoint index sets representing a nontrivial partition of the outcome slots of the given condition. E.g. A|B and C but not A|B and B|C (is not disjoint). Each element's a number which, together with the condition, represents the outcome collection. E.g. 0b110 is A|B, 0b010 is B, etc.
    /// @param amount The amount of collateral or stake to split.
    function splitPosition(
        IERC20 collateralToken,
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint[] calldata partition,
        uint amount
    ) external;

    function mergePositions(
        IERC20 collateralToken,
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint[] calldata partition,
        uint amount
    ) external;

    function redeemPositions(
        IERC20 collateralToken,
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint[] calldata indexSets
    ) external;

    /// @dev Gets the outcome slot count of a condition.
    /// @param conditionId ID of the condition.
    /// @return Number of outcome slots associated with a condition, or zero if condition has not been prepared yet.
    function getOutcomeSlotCount(bytes32 conditionId) external view returns (uint);

    /// @dev Constructs a condition ID from an oracle, a question ID, and the outcome slot count for the question.
    /// @param oracle The account assigned to report the result for the prepared condition.
    /// @param questionId An identifier for the question to be answered by the oracle.
    /// @param outcomeSlotCount The number of outcome slots which should be used for this condition. Must not exceed 256.
    function getConditionId(address oracle, bytes32 questionId, uint outcomeSlotCount) external pure returns (bytes32);

    /// @dev Constructs an outcome collection ID from a parent collection and an outcome collection.
    /// @param parentCollectionId Collection ID of the parent outcome collection, or bytes32(0) if there's no parent.
    /// @param conditionId Condition ID of the outcome collection to combine with the parent outcome collection.
    /// @param indexSet Index set of the outcome collection to combine with the parent outcome collection.
    function getCollectionId(
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint indexSet
    ) external view returns (bytes32);

    /// @dev Constructs a position ID from a collateral token and an outcome collection. These IDs are used as the ERC-1155 ID for this contract.
    /// @param collateralToken Collateral token which backs the position.
    /// @param collectionId ID of the outcome collection associated with this position.
    function getPositionId(IERC20 collateralToken, bytes32 collectionId) external pure returns (uint);
}

contracts/IPrecogMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

interface IPrecogMarket {
    // Returns the start timestamp of the market
    function startTimestamp() external view returns (uint256);

    // Returns the end timestamp of the market
    function endTimestamp() external view returns (uint256);

    // Returns the condition identifier of the market
    function condition() external view returns (bytes32);

    // Returns the address of the token used in the market
    function token() external view returns (address);

    // Returns the address of the current owner
    function owner() external view returns (address);

    // Returns information about the shares owned by an account
    function accountShares(address _account)
        external
        view
        returns (
            uint256 buys,
            uint256 outcomeOneBalance,
            uint256 outcomeTwoBalance,
            uint256 deposited,
            uint256 withdrawn,
            uint256 claimed
        );

    // Initialize the market contract (needed for support cloning)
    function initialize(address _ct, address _token) external;

    // Setup the market with the specified parameters
    function setup(
        address _oracle,
        bytes32 _questionId,
        uint256 _numOutcomes,
        uint256 _subsidy,
        uint256 _overround
    ) external;

    // Allows buying outcome tokens for the specified outcome
    function buy(uint256 _outcome, int128 _amount) external returns (int128);

    // Allows buying outcome tokens for an specified account (restricted to only Self or Owner)
    function _buy(uint256 _outcome, int128 _amount, address _account) external returns (int128);

    // Updates the start and end timestamps of the market
    function updateDates(uint256 _startTimestamp, uint256 _endTimestamp) external;

    // Withdraws remaining tokens from the market after it's resolved
    function withdraw() external;

    // Returns the current cost of the market
    function cost() external view returns (int128);

    // Returns the cost of the market after a proposed transaction
    function cost_after_buy(uint256 _outcome, int128 _amount) external view returns (int128);

    // Returns the price of buying a specified amount of outcome tokens
    function price(uint256 _outcome, int128 _amount) external view returns (int128);

    // Helper function to convert price in token cost
    function getTokenWei(address _token, int128 _amount) external view returns (uint);

    // Helper function to convert token amount to initial funding (on int128)
    function getTokenEth(address _token, uint _amount) external view returns (int128);

    // Returns information about the market
    function getMarketInfo()
        external
        view
        returns (
            int128 totalShares,
            int128 outcomeOneShares,
            int128 outcomeTwoShares,
            int128 currentCost,
            uint256 totalBuys,
            int128 b
        );

    // Returns the address of the ConditionalTokens contract
    function getConditionalTokens() external view returns (address);
}

contracts/IPrecogToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// Interface for PrecogToken contract
interface IPrecogToken is IERC20 {
    // Function to mint new tokens and assign to the specified address
    // Access restricted to the contract owner
    function mint(address to, uint256 amount) external;

    // Function to burn tokens from the specified address
    // Access restricted to the contract owner
    function burn(address from, uint256 amount) external;

    // Function to transfer tokens from one address to another
    // Access restricted to the contract owner
    function move(address from, address to, uint256 amount) external;

    // Function to transfer ownership of the contract to a new address
    // Access restricted to the current owner
    function transferOwnership(address newOwner) external;

    // Returns the address of the current owner
    function owner() external view returns (address);

    // Emitted when ownership of the contract is transferred to a new address
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    // Emitted when tokens are burned
    event Burn(address indexed from, uint256 value);
}

contracts/PrecogMarketV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

/// @title An implementation for liquidity-sensitive LMSR market maker in Solidity
/// @author Marto (based on Abdulla Al-Kamil LsLMSR implementation)
/// @dev Feel free to make any adjustments to the code

import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import {ABDKMath} from "./ABDKMath64x64.sol";
import "./ConditionalTokens.sol";

contract PrecogMarketV2 is IERC1155Receiver, Ownable {
    /**
     * @dev: the contract uses the ABDKMath library to allow for math functions such as logarithms and exponents.
     * As such, all the sensitive state variables are stored as int128(signed 64.64 bit fixed point number).
     */
    using SafeERC20 for IERC20;

    struct ShareInfo {
        uint256 buys;
        uint256 outcomeOneBalance;
        uint256 outcomeTwoBalance;
        uint256 deposited;
        uint256 withdrawn;
        uint256 claimed;
    }

    uint256 public startTimestamp;
    uint256 public endTimestamp;
    bytes32 public condition;
    address public token;
    mapping(address => ShareInfo) public accountShares;

    uint public numOutcomes;
    int128[] private q;
    int128 private b;
    int128 private alpha;
    int128 private currentCost;
    int128 private totalShares;
    uint private totalBuys;
    ConditionalTokens private CT;
    bool private initialized;

    modifier onlyAfterInit {
        require(initialized == true);
        _;
    }

    modifier onlyOwnerOrSelf() {
        require(_msgSender() == owner() || _msgSender() == address(this), "Ownable: caller is not the owner or self");
        _;
    }
    /**
     * @notice Constructor function for the market maker
     * @param _ct The address for the deployed conditional tokens contract
     * @param _token Which ERC-20 token will be used to purchase and redeem outcome tokens for this condition
     */
    constructor(address _ct, address _token) {
        CT = ConditionalTokens(_ct);
        token = _token;
    }

    /**
     * @notice Set up some of the variables for the market maker
     * @param _oracle The address for the EOA/contract which will act as the oracle for this condition
     * @param _questionId The question ID (needs to be unique)
     * @param _numOutcomes The number of different outcomes available
     * _subsidyToken Which ERC-20 token will be used to purchase and redeem outcome tokens for this condition
     * @param _subsidy How much initial funding is used to seed the market maker.
     * @param _overround How much 'profit' does the AMM claim? Note that this is
     * represented in bips. Therefore inputting 300 represents 0.30%
     */
    function setup(
        address _oracle,
        bytes32 _questionId,
        uint _numOutcomes,
        uint _subsidy,
        uint _overround
    ) public onlyOwner {
        require(initialized == false, 'Already initialized');
        require(_overround > 0, 'Cannot have 0 overround');
        CT.prepareCondition(_oracle, _questionId, _numOutcomes);
        condition = CT.getConditionId(_oracle, _questionId, _numOutcomes);

        IERC20(token).safeTransferFrom(msg.sender, address(this), _subsidy);

        numOutcomes = _numOutcomes;
        int128 n = ABDKMath.fromUInt(_numOutcomes);
        int128 initial_subsidy = getTokenEth(token, _subsidy);

        int128 overround = ABDKMath.divu(_overround, 10000); // Note: if the overround is too low, then exp overflows
        alpha = ABDKMath.div(overround, ABDKMath.mul(n, ABDKMath.ln(n)));
        b = ABDKMath.mul(ABDKMath.mul(initial_subsidy, n), alpha);

        for (uint i = 0; i < _numOutcomes; i++) {
            q.push(initial_subsidy);
        }

        initialized = true;
        totalShares = ABDKMath.mul(initial_subsidy, n);
        currentCost = cost();
    }

    /**
     * @notice This function is used to buy outcome tokens.
     * @param _outcome The outcome(s) which a user is buying tokens for.
     * Note: This is the integer representation for the bit array.
     * @param _amount This is the number of outcome tokens purchased
     * @return _price The cost to purchase _amount number of tokens
     */
    function buy(uint256 _outcome, int128 _amount) public onlyAfterInit returns (int128 _price) {
        // If enabled, check if market already started
        if (startTimestamp > 0) {
            require(block.timestamp >= startTimestamp, 'Market not started');
        }

        // If enabled, check if market still open
        if (endTimestamp > 0) {
            require(block.timestamp <= endTimestamp, 'Market already ended');
        }

        // Send call to internal
        return _buy(_outcome, _amount, msg.sender);
    }

    function _buy(uint256 _outcome, int128 _amount, address account) public onlyOwnerOrSelf returns (int128 _price) {
        // Special buy function to Buy tokens f
        require(_outcome > 0, 'Invalid outcome');
        require(CT.payoutDenominator(condition) == 0, 'Market already resolved');

        for (uint j = 0; j < numOutcomes; j++) {
            // Note: (1 << j) is equal to (1 x 2 ** j)
            if ((_outcome & (1 << j)) != 0) {
                q[j] = ABDKMath.add(q[j], _amount);
                totalShares = ABDKMath.add(totalShares, _amount);
            }
        }

        // Calculate new Beta parameter (taking into account new total_shares)
        b = ABDKMath.mul(totalShares, alpha);
        totalBuys += 1;

        int128 sum_total;
        for (uint i = 0; i < numOutcomes; i++) {
            sum_total = ABDKMath.add(sum_total, ABDKMath.exp(ABDKMath.div(q[i], b)));
        }

        int128 new_cost = ABDKMath.mul(b, ABDKMath.ln(sum_total));
        _price = ABDKMath.sub(new_cost, currentCost);
        currentCost = new_cost;

        uint token_cost = getTokenWei(token, _price);
        uint outcomeShares = getTokenWei(token, _amount);
        uint position = CT.getPositionId(IERC20(token), CT.getCollectionId(bytes32(0), condition, _outcome));

        // Get token_cost amount of token from sender (as current payment)
        require(IERC20(token).transferFrom(msg.sender, address(this), token_cost), 'Error transferring tokens');

        if (CT.balanceOf(address(this), position) < outcomeShares) {
            IERC20(token).approve(address(CT), getTokenWei(token, _amount));
            CT.splitPosition(
                IERC20(token), bytes32(0), condition, getPositionAndDustPositions(_outcome), outcomeShares
            );
        }
        // Give n_outcome_tokens of shares to current account (as shares bought)
        CT.safeTransferFrom(address(this), account, position, outcomeShares, '');

        // Register account buy
        accountShares[account].buys += 1;
        if (_outcome == 1) {
            accountShares[account].outcomeOneBalance += outcomeShares;
        }
        if (_outcome == 2) {
            accountShares[account].outcomeTwoBalance += outcomeShares;
        }
        accountShares[account].deposited += token_cost;
    }

    function updateDates(uint256 _startTimestamp, uint256 _endTimestamp) public onlyOwner {
        startTimestamp = _startTimestamp;
        endTimestamp = _endTimestamp;
    }

    function withdraw() public onlyAfterInit onlyOwner {
        require(CT.payoutDenominator(condition) != 0, 'Market needs to be resolved');
        uint[] memory dust = new uint256[](numOutcomes);
        for (uint i = 0; i < numOutcomes; i++) {
            dust[i] = 1 << i;
        }
        CT.redeemPositions(IERC20(token), bytes32(0), condition, dust);
        IERC20(token).safeTransfer(msg.sender, IERC20(token).balanceOf(address(this)));
    }

    function getOnes(uint n) internal pure returns (uint count) {
        while (n != 0) {
            n = n & (n - 1);
            count++;
        }
        return count;
    }

    function getPositionAndDustPositions(uint _outcome) public view returns (uint256[] memory) {
        uint index = (1 << numOutcomes) - 1;
        uint inv = _outcome ^ index;
        uint[] memory partx = new uint256[](getOnes(inv) + 1);
        uint n = 1;
        partx[0] = _outcome;
        for (uint i = 0; i < numOutcomes; i++) {
            // Note: (1 << i) is equal to (1 x 2 ** i)
            if ((inv & 1 << i) != 0) {
                partx[n] = 1 << i;
                n++;
            }
        }
        return partx;
    }

    /**
     *  @notice View function returning the cost function.
     *  This function returns the cost for this inventory state. It will be able
     *  to tell you the total amount of collateral spent within the market maker.
     *  For example, if a pool was seeded with 100 DAI and then a further 20 DAI
     *  has been spent, this function will return 120 DAI.
     */
    function cost() public view onlyAfterInit returns (int128) {
        int128 sum_total;
        for (uint i = 0; i < numOutcomes; i++) {
            sum_total = ABDKMath.add(sum_total, ABDKMath.exp(ABDKMath.div(q[i], b)));
        }
        return ABDKMath.mul(b, ABDKMath.ln(sum_total));
    }

    /**
     *  This function will tell you the cost (similar to above) after a proposed transaction.
     */
    function cost_after_buy(uint256 _outcome, int128 _amount) public view returns (int128) {
        int128 sum_total;
        int128[] memory newq = new int128[](q.length);
        int128 TB = totalShares;

        for (uint j = 0; j < numOutcomes; j++) {
            // Note: (1 << j) is equal to (1 x 2 ** j)
            if ((_outcome & (1 << j)) != 0) {
                newq[j] = ABDKMath.add(q[j], _amount);
                TB = ABDKMath.add(TB, _amount);
            } else {
                newq[j] = q[j];
            }
        }

        int128 _b = ABDKMath.mul(TB, alpha);

        for (uint i = 0; i < numOutcomes; i++) {
            sum_total = ABDKMath.add(sum_total, ABDKMath.exp(ABDKMath.div(newq[i], _b)));
        }

        return ABDKMath.mul(_b, ABDKMath.ln(sum_total));
    }

    /**
     *  This function tells you how much it will cost to make a particular trade.
     *  It does this by calculating the difference between the current cost and the cost after the transaction.
     */
    function price(uint256 _outcome, int128 _amount) public view returns (int128) {
        return cost_after_buy(_outcome, _amount) - currentCost;
    }

    function getMarketInfo() public view returns (int128, int128, int128, int128, uint256, int128) {
        if (q.length < 1) {
            return (0, 0, 0, 0, 0, 0);
        }
        return (totalShares, q[0], q[1], currentCost, totalBuys, b);
    }

    function getConditionalTokens() public view returns (address) {
        return address(CT);
    }

    function getTokenWei(address _token, int128 _amount) internal view returns (uint) {
        uint d = ERC20(_token).decimals();
        return ABDKMath.mulu(_amount, 10 ** d);
    }

    function getTokenEth(address _token, uint _amount) internal view returns (int128) {
        uint d = ERC20(_token).decimals();
        return ABDKMath.divu(_amount, 10 ** d);
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external override pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external override pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    function supportsInterface(bytes4 interfaceId) external override pure returns (bool) {
        return false;
    }
}

contracts/PrecogMarketV3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

/// @title An implementation for liquidity-sensitive LMSR market maker in Solidity
/// @author Marto (based on Abdulla Al-Kamil LsLMSR implementation)
/// @dev Feel free to make any adjustments to the code

import "@openzeppelin/contracts/token/ERC1155/IERC1155Receiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import {ABDKMath} from "./ABDKMath64x64.sol";
import "./ConditionalTokens.sol";

contract PrecogMarketV3 is IERC1155Receiver {
    /**
     * @dev: the contract uses the ABDKMath library to allow for math functions such as logarithms and exponents.
     * As such, all the sensitive state variables are stored as int128(signed 64.64 bit fixed point number).
     */
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct ShareInfo {
        uint256 buys;
        uint256 outcomeOneBalance;
        uint256 outcomeTwoBalance;
        uint256 deposited;
        uint256 withdrawn;
        uint256 claimed;
    }

    uint256 public startTimestamp;
    uint256 public endTimestamp;
    bytes32 public condition;
    address public token;
    address public owner;
    mapping(address => ShareInfo) public accountShares;

    uint public numOutcomes;
    int128[] private q;
    int128 private b;
    int128 private alpha;
    int128 private currentCost;
    int128 private totalShares;
    uint private totalBuys;
    ConditionalTokens private CT;
    bool private initialized;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyAfterInit {
        require(initialized == true);
        _;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyOwnerOrSelf() {
        require(msg.sender == owner || msg.sender == address(this), "Only owner or internal");
        _;
    }
    /**
     * @notice Constructor like function for the market maker
     * @param _ct The address for the deployed conditional tokens contract
     * @param _token Which ERC-20 token will be used to purchase and redeem outcome tokens for this condition
     */
    function initialize(address _ct, address _token) public {
        require(owner == address(0), 'Already initialized');
        owner = msg.sender;
        token = _token;
        CT = ConditionalTokens(_ct);
    }

    /**
     * @notice Set up some of the variables for the market maker
     * @param _oracle The address for the EOA/contract which will act as the oracle for this condition
     * @param _questionId The question ID (needs to be unique)
     * @param _numOutcomes The number of different outcomes available
     * _subsidyToken Which ERC-20 token will be used to purchase and redeem outcome tokens for this condition
     * @param _subsidy How much initial funding is used to seed the market maker.
     * @param _overround How much 'profit' does the AMM claim? Note that this is
     * represented in bips. Therefore inputting 300 represents 0.30%
     */
    function setup(
        address _oracle,
        bytes32 _questionId,
        uint _numOutcomes,
        uint _subsidy,
        uint _overround
    ) public onlyOwner {
        require(initialized == false, 'Already initialized');
        require(_overround > 0, 'Cannot have 0 overround');
        CT.prepareCondition(_oracle, _questionId, _numOutcomes);
        condition = CT.getConditionId(_oracle, _questionId, _numOutcomes);

        IERC20(token).safeTransferFrom(msg.sender, address(this), _subsidy);

        numOutcomes = _numOutcomes;
        int128 initialSubsidy = getTokenEth(token, _subsidy);
        int128 n = ABDKMath.fromUInt(_numOutcomes);
        int128 overround = ABDKMath.divu(_overround, 10000); // Note: if the overround is too low, then exp overflows
        alpha = ABDKMath.div(overround, ABDKMath.mul(n, ABDKMath.ln(n)));
        b = ABDKMath.mul(ABDKMath.mul(initialSubsidy, n), alpha);
        for (uint i = 0; i < _numOutcomes; i++) {
            q.push(initialSubsidy);
        }

        initialized = true;

        // After initialization, pre calculate current totals and cost
        totalShares = ABDKMath.mul(initialSubsidy, n);
        currentCost = cost();
    }

    /**
     * @notice This function is used to buy outcome tokens.
     * @param _outcome The outcome(s) which a user is buying tokens for.
     * Note: This is the integer representation for the bit array.
     * @param _amount This is the number of outcome tokens purchased
     * @return _price The cost to purchase _amount number of tokens
     */
    function buy(uint256 _outcome, int128 _amount) public onlyAfterInit returns (int128 _price) {
        // If enabled, check if market already started
        if (startTimestamp > 0) {
            require(block.timestamp >= startTimestamp, 'Market not started');
        }

        // If enabled, check if market still open
        if (endTimestamp > 0) {
            require(block.timestamp <= endTimestamp, 'Market already ended');
        }

        // Send call to internal
        return _buy(_outcome, _amount, msg.sender);
    }

    function _buy(uint256 _outcome, int128 _amount, address _account) public onlyOwnerOrSelf returns (int128 _price) {
        // Special buy function to Buy tokens f
        require(_outcome > 0, 'Invalid outcome');
        require(CT.payoutDenominator(condition) == 0, 'Market already resolved');

        for (uint j = 0; j < numOutcomes; j++) {
            // Note: (1 << j) is equal to (1 x 2 ** j)
            if ((_outcome & (1 << j)) != 0) {
                q[j] = ABDKMath.add(q[j], _amount);
                totalShares = ABDKMath.add(totalShares, _amount);
            }
        }

        // Calculate new Beta parameter (taking into account new total_shares)
        b = ABDKMath.mul(totalShares, alpha);

        int128 sum_total;
        for (uint i = 0; i < numOutcomes; i++) {
            sum_total = ABDKMath.add(sum_total, ABDKMath.exp(ABDKMath.div(q[i], b)));
        }

        // Calculate new cost and
        int128 new_cost = ABDKMath.mul(b, ABDKMath.ln(sum_total));
        _price = ABDKMath.sub(new_cost, currentCost);
        currentCost = new_cost;

        // Get token_cost amount of token from sender (as current payment)
        uint tokenCost = getTokenWei(token, _price);
        require(IERC20(token).transferFrom(msg.sender, address(this), tokenCost), 'Error transferring tokens');
        uint outcomeShares = getTokenWei(token, _amount);

        uint position = CT.getPositionId(IERC20(token), CT.getCollectionId(bytes32(0), condition, _outcome));
        if (CT.balanceOf(address(this), position) < outcomeShares) {
            IERC20(token).approve(address(CT), outcomeShares);
            uint[] memory partition = getPositionAndDustPositions(_outcome);
            CT.splitPosition(IERC20(token), bytes32(0), condition, partition, outcomeShares);
        }
        // Give n_outcome_tokens of shares to current account (as shares bought)
        CT.safeTransferFrom(address(this), _account, position, outcomeShares, '');

        // Register account buy
        totalBuys = totalBuys.add(1);
        accountShares[_account].buys = accountShares[_account].buys.add(1);
        if (_outcome == 1) {
            accountShares[_account].outcomeOneBalance = accountShares[_account].outcomeOneBalance.add(outcomeShares);
        } else if (_outcome == 2) {
            accountShares[_account].outcomeTwoBalance = accountShares[_account].outcomeTwoBalance.add(outcomeShares);
        }
        accountShares[_account].deposited = accountShares[_account].deposited.add(tokenCost);
    }

    function updateDates(uint256 _startTimestamp, uint256 _endTimestamp) public onlyOwner {
        startTimestamp = _startTimestamp;
        endTimestamp = _endTimestamp;
    }

    function withdraw() public onlyAfterInit onlyOwner {
        require(CT.payoutDenominator(condition) != 0, 'Market needs to be resolved');
        uint[] memory dust = new uint256[](numOutcomes);
        for (uint i = 0; i < numOutcomes; i++) {
            dust[i] = 1 << i;
        }
        CT.redeemPositions(IERC20(token), bytes32(0), condition, dust);
        IERC20(token).safeTransfer(msg.sender, IERC20(token).balanceOf(address(this)));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }

    function getOnes(uint n) internal pure returns (uint count) {
        while (n != 0) {
            n = n & (n - 1);
            count++;
        }
        return count;
    }

    function getPositionAndDustPositions(uint _outcome) public view returns (uint256[] memory) {
        uint index = (1 << numOutcomes) - 1;
        uint inv = _outcome ^ index;
        uint[] memory partx = new uint256[](getOnes(inv) + 1);
        uint n = 1;
        partx[0] = _outcome;
        for (uint i = 0; i < numOutcomes; i++) {
            // Note: (1 << i) is equal to (1 x 2 ** i)
            if ((inv & 1 << i) != 0) {
                partx[n] = 1 << i;
                n++;
            }
        }
        return partx;
    }

    /**
     *  @notice View function returning the cost function.
     *  This function returns the cost for this inventory state. It will be able
     *  to tell you the total amount of collateral spent within the market maker.
     *  For example, if a pool was seeded with 100 DAI and then a further 20 DAI
     *  has been spent, this function will return 120 DAI.
     */
    function cost() public view onlyAfterInit returns (int128) {
        int128 sum_total;
        for (uint i = 0; i < numOutcomes; i++) {
            sum_total = ABDKMath.add(sum_total, ABDKMath.exp(ABDKMath.div(q[i], b)));
        }
        return ABDKMath.mul(b, ABDKMath.ln(sum_total));
    }

    /**
     *  This function will tell you the cost (similar to above) after a proposed transaction.
     */
    function cost_after_buy(uint256 _outcome, int128 _amount) public view returns (int128) {
        int128 sumTotal;
        int128[] memory newQ = new int128[](q.length);
        int128 newTotalShares = totalShares;

        // Add new amount of shares to received outcome
        for (uint j = 0; j < numOutcomes; j++) {
            // Note: (1 << j) is equal to (1 x 2 ** j)
            if ((_outcome & (1 << j)) != 0) {
                newQ[j] = ABDKMath.add(q[j], _amount);
                newTotalShares = ABDKMath.add(newTotalShares, _amount);
            } else {
                newQ[j] = q[j];
            }
        }

        // Calculate new cost based on newTotalShares and newQ values
        int128 newB = ABDKMath.mul(newTotalShares, alpha);
        for (uint i = 0; i < numOutcomes; i++) {
            sumTotal = ABDKMath.add(sumTotal, ABDKMath.exp(ABDKMath.div(newQ[i], newB)));
        }
        return ABDKMath.mul(newB, ABDKMath.ln(sumTotal));
    }

    /**
     *  This function tells you how much it will cost to make a particular trade.
     *  It does this by calculating the difference between the current cost and the cost after the transaction.
     */
    function price(uint256 _outcome, int128 _amount) public view returns (int128) {
        return ABDKMath.sub(cost_after_buy(_outcome, _amount), currentCost);
    }

    function getMarketInfo() public view returns (int128, int128, int128, int128, uint256, int128) {
        if (q.length < 1) {
            return (0, 0, 0, 0, 0, 0);
        }
        return (totalShares, q[0], q[1], currentCost, totalBuys, b);
    }

    function getConditionalTokens() public view returns (address) {
        return address(CT);
    }

    function getTokenWei(address _token, int128 _amount) public view returns (uint) {
        uint d = ERC20(_token).decimals();
        return ABDKMath.mulu(_amount, 10 ** d);
    }

    function getTokenEth(address _token, uint _amount) public view returns (int128) {
        uint d = ERC20(_token).decimals();
        return ABDKMath.divu(_amount, 10 ** d);
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external override pure returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external override pure returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    function supportsInterface(bytes4 interfaceId) external override pure returns (bool) {
        return false;
    }
}

contracts/PrecogMasterV1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.6;
pragma abicoder v2;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./PrecogToken.sol";

/**
 * @title PrecogMasterV1
 * @dev Contract to manage Precog token claims and PredictionMarkets
 */
contract PrecogMasterV1 is AccessControl {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CALLER_ROLE = keccak256("CALLER_ROLE");
    address public token;

    struct TokenClaim {
        address account;
        uint256 user;
        uint256 claimedAmount;
        uint256 claimedTimestamp;
    }

    struct MiningSeason {
        uint256 startTimestamp;
        uint256 endTimestamp;
        uint256 maxUserClaim;
        uint256 maxTotalClaim;
        uint256 maxTotalMint;
        uint256 claimedAmount;
        uint256 mintedAmount;
    }

    mapping(address => TokenClaim) public accountTokenClaims;
    mapping(uint256 => TokenClaim) public userTokenClaims;
    mapping(uint256 => MiningSeason) public miningSeasons;
    uint256 public currentSeason;

    event TokensClaimed(address indexed account, uint256 indexed user, uint256 amount, uint256 timestamp);
    event TokensMinted(address indexed account, uint256 amount, uint256 timestamp);

    constructor(address precogToken, address initialAdmin) {
        // Grant DEFAULT_ADMIN_ROLE to the initial admin (this is the admin to the ADMIN_ROLE list)
        _setupRole(DEFAULT_ADMIN_ROLE, initialAdmin);
        // Set ADMIN_ROLE as admin of CALLER_ROLE list
        _setRoleAdmin(CALLER_ROLE, ADMIN_ROLE);
        // Set already deployed precogToken as claimable token and collateral token for all Markets
        token = precogToken;
        // Grant ADMIN_ROLE to initial Admin (this enables to call "addAdmin" helper function)
        _setupRole(ADMIN_ROLE, initialAdmin);
    }

    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "Restricted to admins");
        _;
    }

    modifier onlyCaller() {
        require(hasRole(CALLER_ROLE, msg.sender), "Restricted to callers");
        _;
    }

    // Public functions and helpers
    function hasClaimed(address account, uint256 user) external view returns (bool) {
        return accountTokenClaims[account].claimedTimestamp != 0 || userTokenClaims[user].claimedTimestamp != 0;
    }

    // Only caller functions
    function claimToken(address account, uint256 user, uint256 amount) external onlyCaller returns (bool) {
        // Validate token claim
        require(accountTokenClaims[account].claimedTimestamp == 0, "Account already claimed");
        require(userTokenClaims[user].claimedTimestamp == 0, "User already claimed");
        MiningSeason storage season = miningSeasons[currentSeason];
        if (season.startTimestamp > 0) {
            require(block.timestamp >= season.startTimestamp, "Season not started");
            require(block.timestamp < season.endTimestamp, "Season already ended");
            require(amount <= season.maxUserClaim, "Invalid user claim amount");
            require(amount + season.claimedAmount <= season.maxTotalClaim, "Season max token claims reached");
            require(amount + season.mintedAmount <= season.maxTotalMint, "Season max token mints reached");
        }

        // Register current token claim
        TokenClaim memory claim = TokenClaim({
            user: user,
            account: account,
            claimedAmount: amount,
            claimedTimestamp: block.timestamp
        });
        accountTokenClaims[account] = claim;
        userTokenClaims[user] = claim;
        season.claimedAmount += amount;
        season.mintedAmount += amount;

        // Mint tokens to received account
        PrecogToken(token).mint(account, amount);

        emit TokensClaimed(account, user, amount, block.timestamp);
        return true;
    }

    // Only admin functions
    function addCaller(address account) external onlyAdmin {
        grantRole(CALLER_ROLE, account);
    }

    function removeCaller(address account) external onlyAdmin {
        revokeRole(CALLER_ROLE, account);
    }

    function addAdmin(address account) external onlyAdmin {
        grantRole(ADMIN_ROLE, account);
    }

    function removeAdmin(address account) external onlyAdmin {
        revokeRole(ADMIN_ROLE, account);
    }

    function updateCurrentSeason(uint256 seasonIndex) external onlyAdmin {
        currentSeason = seasonIndex;
    }

    function setMiningSeason(
        uint256 seasonIndex,
        uint256 startTimestamp,
        uint256 endTimestamp,
        uint256 maxUserClaim,
        uint256 maxTotalClaim,
        uint256 maxTotalMint,
        uint256 claimedAmount,
        uint256 mintedAmount
    ) external onlyAdmin {
        miningSeasons[seasonIndex] = MiningSeason({
            startTimestamp: startTimestamp,
            endTimestamp: endTimestamp,
            maxUserClaim: maxUserClaim,
            maxTotalClaim: maxTotalClaim,
            maxTotalMint: maxTotalMint,
            claimedAmount: claimedAmount,
            mintedAmount: mintedAmount
        });
    }

    function precogMint(address to, uint256 amount) external onlyAdmin {
        // Validate token mint
        MiningSeason storage season = miningSeasons[currentSeason];
        if (season.startTimestamp > 0) {
            require(amount + season.mintedAmount <= season.maxTotalMint, "Season max token mints reached");
        }

        // Register token mint
        season.mintedAmount += amount;

        // Mint tokens to received account
        PrecogToken(token).mint(to, amount);

        emit TokensMinted(to, amount, block.timestamp);
    }

    function precogBurn(address from, uint256 amount) external onlyAdmin {
        // WIP Here we should decrease `season.mintedAmount` for the current season
        PrecogToken(token).burn(from, amount);
    }

    function precogTransferOwnership(address newPrecogMaster) external onlyAdmin {
        PrecogToken(token).transferOwnership(newPrecogMaster);
    }

}

